import React, { Component } from 'react'

const emailReg = RegExp(/^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/)

export default class Passwordrec extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            formErrors: {
                email: ""
            }
        }
    }

    handleSubmit = e => {
        e.preventDefault();
        const userdata = {
            email: this.state.email
        }
    }

    handleChange = e => {
        e.preventDefault
    }
    render() {
        return (
            <div className="modal">
                <label htmlFor="password-rec">Enter you email address:</label>
                <input type="email" name="email" id="password-rec" onChange={this.handleChange} placeholder="Email"/>
                <button className="btn-primary">Submit</button>
            </div>
        )
    }
}
