import React, { useState } from 'react'
import Nav from './Nav'

export default function Header({children,hero}) {

  const [theposition, setThePosition] = useState({
    theposition: 0
  });

    window.onscroll = () => {
      handleScroll();
    }

    const handleScroll = () => {

      let isNav;
      setThePosition({
          theposition: window.pageYOffset
        });

        if ( theposition >= 480 ) {
          isNav = true;
          alert(theposition)
        } else if( theposition < 480) {
          isNav = false;
          alert(theposition)
        }

        console.log(theposition, isNav);
    }

    return (
      <header className={hero} >
        <div className="bg-color">
          <div className="container">
            <Nav />
            {children}
          </div>
        </div>
      </header>
    );
}

Header.defaultProps = {
    hero:"defaultHero"
};
