import React, { Component } from 'react'
import { Link } from 'react-router-dom'

import AOS from 'aos';
import 'aos/dist/aos.css';
import {FaProjectDiagram, FaNetworkWired, FaServicestack, GoProject} from 'react-icons/all'

const icon1 = () => {
    return <svg width="24" height="24" xmlns="http://www.w3.org/2000/svg" fill-rule="evenodd" clip-rule="evenodd"><path d="M9.484 15.696l-.711-.696-2.552 2.607-1.539-1.452-.698.709 2.25 2.136 3.25-3.304zm0-5l-.711-.696-2.552 2.607-1.539-1.452-.698.709 2.25 2.136 3.25-3.304zm0-5l-.711-.696-2.552 2.607-1.539-1.452-.698.709 2.25 2.136 3.25-3.304zm10.516 11.304h-8v1h8v-1zm0-5h-8v1h8v-1zm0-5h-8v1h8v-1zm4-5h-24v20h24v-20zm-1 19h-22v-18h22v18z"/></svg>
}

export default class Service extends Component {
    constructor(props) {
        super(props);
        this.state = {
            services: [
            {
                index:0,
                icon:<svg width="70" height="24" xmlns="http://www.w3.org/2000/svg" fill-rule="evenodd" clip-rule="evenodd"><path d="M9.484 15.696l-.711-.696-2.552 2.607-1.539-1.452-.698.709 2.25 2.136 3.25-3.304zm0-5l-.711-.696-2.552 2.607-1.539-1.452-.698.709 2.25 2.136 3.25-3.304zm0-5l-.711-.696-2.552 2.607-1.539-1.452-.698.709 2.25 2.136 3.25-3.304zm10.516 11.304h-8v1h8v-1zm0-5h-8v1h8v-1zm0-5h-8v1h8v-1zm4-5h-24v20h24v-20zm-1 19h-22v-18h22v18z"/></svg>,
                title: "Courses",
                subtitle: "Learn anywhere, anytime, at your own pace",
                description: "We offer free courses but one can opt to join premium membership to get more resources on the course.",
                link: "/service1"
            },
            {
                index:1,
                icon:<FaProjectDiagram className="service-icon"/>,
                title: "Projects",
                subtitle: "subservice 2",
                description: "description for service 2... a whole bunch of paragraph",
                link: "/service1"
            },
            {
                index:2,
                icon:<FaServicestack className="service-icon"/>,
                title: "Mentorship",
                subtitle: "subservice 3",
                description: "description for service 3... a whole bunch of paragraph",
                link: "/service1"
            },
            {
                index:3,
                icon:<GoProject className="service-icon"/>,
                title: "Community hub",
                subtitle: "subservice 4",
                description: "description for service 4... a whole bunch of paragraph",
                link: "/service1"
            }
        ]
    }
}

    componentDidMount(){
       AOS.init({
         duration : 1000
        })
    }

    // use tinypng.com for compressing all images I'll use and help kilimoshare.com image loading

    render() {
        return (
            <div className='service' id='service'>
               <div className="container">
                    <h1 className="header">Services</h1>
                   <div className="services-showcase">{this.state.services.map((item,index) => {
                       return <article key={index} data-aos="fade-up" data-aos-duration="1500" data-aos-delay={item.index*300}>
                                <div className="front-face">
                                    {item.icon}
                                    <h2>{item.title}</h2>
                                </div>
                                <div className="hovered-face">
                                    <h3>
                                        {item.subtitle}
                                    </h3>
                                    <p>
                                        {item.description}
                                    </p>
                                    <Link to="/">Learn more</Link>
                                </div>
                       </article>
                       })}
                   </div>
               </div>
            </div>
        )
    }
}
