import React, { Component } from 'react'
import dummy from "../../images/dummy.jpeg"
import { FaQuoteLeft, FaTwitter } from "react-icons/all"

export default class Testimonials extends Component {
    constructor (props) {
        super(props);
        this.state = {
            testimonies: [
                {
                    index: 0,
                    quote: "Lorem ipsum dolor sit amet <a href='www.twitter.com'>@codemunit</a> consectetur adipisicing elit. Asperiores, similique! Aperiam ducimus molestias consectetur neque temporibus eius iure ullam necessitatibus accusantium fugiat! Magnam maxime minus illo veritatis cumque quis excepturi.",
                    twitterSrc: "/",
                    name: "John doe",
                    twitterUsername: "johndoe",

                }
            ]
        }
    }

    render() {
        return (
            <div className="testimonials">
                <div className="container">
                    <h1 className="header">Testimonial</h1>
                    <div className="show-case">
                        {this.state.testimonies.map((item, index) => {
                            return <article key={index} data-aos="fade-up" data-aos-duration="1500" data-aos-delay="100">
                                <img src={dummy} alt="" />
                                <p>
                                    <FaQuoteLeft className="icon" />

                                </p>
                                <div className="witness">
                                    <a href={item.twitterSrc}><FaTwitter /> @{item.twitterUsername}</a>
                                    <small>{item.name}</small>
                                </div>
                            </article>
                        })}
                    </div>
                </div>
            </div>
        )
    }
}
