import React, { Component } from 'react'
import { Link } from "react-router-dom"
import { FaFacebookF, FaGooglePlusG } from "react-icons/all"

const emailReg = RegExp(/^A[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\z/)

const formValid = formErrors => {
    let valid = true;

    Object.values(formErrors).forEach(val => {
        val.length > 0 && (valid = false);
    });

    return valid;
}

export default class Signup extends Component {
    constructor (props) {
        super(props);
        this.state = {
            name: null,
            username: null,
            email: null,
            password: null,
            confirmPassword: null,
            formErrors: {
                name: null,
                username: null,
                email: null,
                password: null,
                confirmPassword: null
            }
        };
    }

    handleSubmit = e => {
        e.preventDefault();

        if (formValid(this.state.formErrors)) {
            console.log(`
                ==SUBMITTING==
                Name: ${this.state.name},
                Username: ${this.state.username},
                Email: ${this.state.email},
                Password: ${this.state.password},
                Confirm Password: ${this.state.confirmPassword}
            `);
        } else {
            console.error(`Form invalid: display the error message`);
        }
    }

    handleChange = e => {
        e.preventDefault();
        const { name, value } = e.target;
        let formErrors = this.state.formErrors;

        switch (name) {
            case 'name':
             formErrors.name = value.length > 0? "name is required":"";
            break;
            case 'username':
             formErrors.username = value.length > 0? "name is required":"";
            break;
            case 'email':
             formErrors.username = value.length > 0? "name is required":"";
            break;
            default:
             break;
        }
    }


    render() {
        return (
            <div className="form-container sign-up-container">
                <form>
                    <h1>Create an account</h1>
                    <div className="social-container">
                        <Link to=""><FaFacebookF className="icon"/></Link>
                        <Link to=""><FaGooglePlusG className="icon"/></Link>
                    </div>
                    <span>or use your email for registration</span>
                    <div className="name">
                        <div className="name-name">
                            <label htmlFor="name">Your Name</label>
                            <input type="text" name="name" onChange={this.handleChange} noValidate/>
                        </div>
                        <div className="name-username">
                            <label htmlFor="username">Username</label>
                            <input type="text" name="username" onChange={this.handleChange}/>
                        </div>
                    </div>
                    <label htmlFor="email">Email</label>
                    <input type="email" name="email" id="email" onChange={this.handleChange}/>
                    <label htmlFor="password">Password</label>
                    <input type="password" name="password" id="password" autoComplete="new-password" onChange={this.handleChange}/>
                    <label htmlFor="confirmPassword">Confirm password</label>
                    <input type="password" name="confirmPassword" id="confirmPassword" onChange={this.handleChange}/>
                    <button className="btn-primary" onClick={this.handleSubmit}>Register</button>
                </form>
            </div>
        )
    }
}
